import React, { useContext, useState } from "react";
import CartContext from "../../store/cart-context";
import Modal from "../UI/Modal/Modal";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";

const Cart = (props) => {
  const [showOrderForm, setShowOrderForm] = useState(false);
  const ctx = useContext(CartContext);

  const addItemCartHandler = (item, amount) => {
    ctx.onAddItem(item, amount);
  };

  const removeItemCartHandler = (id) => {
    ctx.onRemoveItem(id);
  };
  const totalAmount = `${ctx.totalAmount.toFixed(2)} $`;

  const handleOrder = () => {
    setShowOrderForm(true);
  };

  return (
    <Modal>
      <ul className={classes["cart-items"]}>
        {ctx.cartItem.map((item) => (
          <CartItem
            key={item.id}
            id={item.id}
            name={item.name}
            price={item.price}
            amount={item.amount}
            onAdd={addItemCartHandler.bind(null, item, 1)}
            onRemove={ctx.onRemoveItem.bind(null, item, item.id)}
          />
        ))}
      </ul>
      <div className={classes.total}>
        <span>totalAmount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button onClick={ctx.onHideCart} className={classes[".button--alt"]}>
          Close
        </button>
        <button className={classes.button} onClick={handleOrder}>
          Order
        </button>

        {showOrderForm && (
          <form className={classes["order_form"]}>
            <div className={classes.control}>
              <label htmlFor="name">Your Name</label>
              <input type="text" id="name" />
            </div>
            <div className={classes.control}>
              <label htmlFor="street">Street</label>
              <input type="text" id="street" />
            </div>
            <div className={classes.control}>
              <label htmlFor="postal">Postal Code</label>
              <input type="text" id="postal" />
            </div>
            <div className={classes.control}>
              <label htmlFor="city">City</label>
              <input type="text" id="city" />
            </div>
            <div className="">
              <button type="submit">Confirm</button>
              <button onClick={() => setShowOrderForm(false)}>Cancel</button>
            </div>
          </form>
        )}
      </div>
    </Modal>
  );
};

export default Cart;
