import { useState } from "react";

import classes from "./OrderForm.module.css";

const OrderForm = ({ onHideOrderForm }) => {
  // const [name, setName] = useState({ value: "", isClicked: false });
  // const [street, setStreet] = useState({ value: "", isClicked: false });
  // const [postal, setPostal] = useState({ name: "", isClicked: false });
  // const [city, setCity] = useState({ name: "", isClicked: false });
  const [inputVal, setInputVal] = useState({
    name: "",
    street: "",
    postal: "",
    city: "",
  });
  const [inputBlur, setInputBlur] = useState({
    name: false,
    street: false,
    postal: false,
    city: false,
  });
  const [formIsValid, setFormIsValid] = useState(false);

  // const isNameValid = name.value.trim() !== "";
  // const isStreetValid = street.value.trim() !== "";
  // // const isPostalValid = postal.value.trim() !== "" && postal.value.length === 5;
  // const isCityValid = city.value.trim() !== "";

  // const handleName = (e) => {
  //   setName({ value: e.target.value, isClicked: false });
  // };
  // const handleStreet = (e) => {
  //   setStreet({ value: e.target.value, isClicked: false });
  // };
  // const handlePostal = (e) => {
  //   setPostal({ value: e.target.value, isClicked: false });
  // };
  // const handleCity = (e) => {
  //   setCity({ value: e.target.value, isClicked: false });
  // };
  const inputValHandler = (e) => {
    setInputVal((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const inputBlurHandler = (e) => {
    setInputBlur((prev) => ({ ...prev, [e.target.id]: true }));
  };

  const submitHandler = (e) => {
    e.preventDefault();
    // const userData = {
    //   name: name.value,
    //   city: city.value,
    //   street: street.value,
    //   postal: postal.value,
    // };
    const userData = {
      name: inputVal.name,
      city: inputVal.city,
      street: inputVal.street,
      postal: inputVal.postal,
    };

    const userBlur = {
      name: inputBlur.name,
      city: inputBlur.city,
      street: inputBlur.street,
      postal: inputBlur.postal,
    };

    // setName("");
    // setStreet("");
    // setPostal("");
    // setCity("");
    setInputVal({
      name: "",
      street: "",
      postal: "",
      city: "",
    });

    console.log(userData);
    console.log(userBlur);
  };

  const isNameValid = inputVal.name.trim() !== "";
  const isStreetValid = inputVal.street.trim() !== "";
  const isPostalValid = inputVal.postal.trim() !== "" && inputVal.postal.length === 5;
  

  // console.log(inputBlur);

  return (
    <form className={classes["order_form"]} onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={inputValHandler}
          onBlur={inputBlurHandler}
        />
      </div>

      <div className={`${classes.control}`}>
        <label htmlFor="street">Street</label>
        <input
          type="text"
          id="street"
          onChange={inputValHandler}
          onBlur={inputBlurHandler}
        />
      </div>
      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input
          type="text"
          id="postal"
          onChange={inputValHandler}
          onBlur={inputBlurHandler}
        />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input
          type="text"
          id="city"
          onChange={inputValHandler}
          onBlur={inputBlurHandler}
        />
      </div>
      <div className={classes.actions}>
        <button type="submit">Confirm</button>
        <button onClick={onHideOrderForm}>Cancel</button>
      </div>
    </form>
  );
};

export default OrderForm;
