import { useState } from "react";

import classes from "./OrderForm.module.css";

const OrderForm = ({ onHideOrderForm }) => {
  const [name, setName] = useState({ value: "", isClicked: false });
  const [street, setStreet] = useState({ value: "", isClicked: false });
  const [postal, setPostal] = useState({ name: "", isClicked: false });
  const [city, setCity] = useState({ name: "", isClicked: false });
  const [formIsValid, setFormIsValid] = useState(false);

  const isNameValid = name.value.trim() !== "";
  const isStreetValid = street.value.trim() !== "";
  const isPostalValid = postal.value.trim()  !== "";
  const isCityValid = city.value.trim() !== "";

  const handleName = (e) => {
    setName({ value: e.target.value, isClicked: false });
  };
  const handleStreet = (e) => {
    setStreet({ value: e.target.value, isClicked: false });
  };
  const handlePostal = (e) => {
    setPostal({ value: e.target.value, isClicked: false });
  };
  const handleCity = (e) => {
    setCity({ value: e.target.value, isClicked: false });
  };

  const handleInput = (e) => {
    setName({ ...name, isClicked: true });
    setStreet({ ...street, isClicked: true });
    setPostal({ ...postal, isClicked: true });
    setCity({ ...city, isClicked: true });
  };

  const submitHandler = (e) => {
    e.preventDefault();
    const userData = {
      name,
      city,
      street,
      postal,
    };

    setName("");
    setStreet("");
    setPostal("");
    setCity("");
    console.log(userData);
  };

  const inputValidate = (input) => {};

  return (
    <form className={classes["order_form"]} onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" onChange={handleName} />
      </div>
      <div className={`${classes.control}`}>
        <label htmlFor="street">Street</label>
        <input type="text" id="street" onChange={handleStreet} />
      </div>
      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input type="text" id="postal" onChange={handlePostal} />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" onChange={handleCity} />
      </div>
      <div className={classes.actions}>
        <button type="submit">Confirm</button>
        <button onClick={onHideOrderForm}>Cancel</button>
      </div>
    </form>
  );
};

export default OrderForm;
